import React from "react";
import {withAuth0} from '@auth0/auth0-react'
const Profile = () => {
  const { user, isAuthenticated, isLoading } = withAuth0();

  if (isLoading) {
    return <div>Loading ...</div>;
  }

  return (
    isAuthenticated && (
      <div>
        <img src={user.picture} alt={user.name} />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
      </div>
    )
  );
};



export default Profile;
